
public class numberswap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//swap number
		
		
		int a = 10;
		int b = 20;
		
		System.out.println("the old value of a and b :" + a);
		System.out.println("the old value of a and b :"+ b);
		
		int c;
		
		c=a;
		a=b;
		b=c;
		
		System.out.println("the new value of a and b :" + a);
		System.out.println("the new value of a and b :" + b);
		
	}

}
